-- 예제14 1. 가장 적은 평균급여를 받는 직책에 대해 그 직책과 평균급여를 나타내시오.
SELECT TITLE, AVG(SALARY) FROM S_EMP GROUP BY TITLE HAVING AVG(SALARY) = (SELECT MIN(AVG_SAL) FROM (SELECT TITLE, AVG(SALARY) AS AVG_SAL FROM S_EMP GROUP BY TITLE));
SELECT * FROM (SELECT TITLE, AVG(SALARY) FROM S_EMP GROUP BY TITLE ORDER BY AVG(SALARY)) WHERE ROWNUM = 1;